#!/bin/bash

# ========== Params ==========
# apartment, office
config=$(rospack find khronos_eval)/config/ground_truth/consolidate_gt.yaml

# ========== Autogenerated arguments ==========
exec_dir=$(catkin_find --first-only --without-underlays --libexec khronos_eval)

# ========== Build the GT files ==========
eval $exec_dir/consolidate_gt $config
